const uint8_t Key8[] = {4, '8', 'T', 'U', 'V'};
const uint8_t Key9[] = {5, '9', 'X', 'Y', 'Z', 'W'};

const uint8_t *KeyPad[] =
    {
        Key0,
        Key1,
        Key2,
        Key3,
        Key4,
        Key5,
        Key6,
        Key7,
        Key8,
        Key9,
};

uint8_t OldKey = 1;

void ProgressEnterKey(void);

void ProgressKey(char Character)
{
    static uint8_t ClkKey = 1;
    // Nhan phim > 2 lan.
    if (OldKey == Character)
    {
        if (xSystem.TimeOut.Clk100ms_KeyPad > 0)
        {
            if (ClkKey < KeyPad[Character - '0'][0])
                ClkKey++;
            else
                ClkKey = 1;
        }
        else
        {
            xSystem.Keypad.Str[xSystem.Keypad.StrLen] = KeyPad[Character - '0'][ClkKey];
            xSystem.Keypad.StrLen++;
            ClkKey = 1;
        }
    }
    else
        ClkKey = 1;
    // Key moi sau Init
    if (OldKey == 1)
    {
        xSystem.Keypad.Str[xSystem.Keypad.StrLen] = KeyPad[Character - '0'][ClkKey];
        xSystem.Keypad.StrLen++;
    }
    else if (OldKey != Character)
    {
        xSystem.Keypad.Str[xSystem.Keypad.StrLen] = KeyPad[Character - '0'][ClkKey];
        xSystem.Keypad.StrLen++;
    }
    else if (xSystem.Keypad.StrLen <= 0)
        xSystem.Keypad.Str[0] = KeyPad[Character - '0'][ClkKey];
    else
        xSystem.Keypad.Str[xSystem.Keypad.StrLen - 1] = KeyPad[Character - '0'][ClkKey];
    OldKey = Character;
    BeepKeypad();
    EDIT_SetText(ID_Text_handle, (char *)xSystem.Keypad.Str);
    xSystem.TimeOut.Clk100ms_KeyPad = xSystem.Parameters.TimeKeypad * 4;
}
