static void ProgressEnterKey(void)
{
    if (xSystem.Keypad.Status == KP_CALCEL)
    {
        switch (xSystem.Keypad.ID_Req)
        {
        case WD_INFO_ENTER:
            Info_Enter_Update_Fr_Keypad();
            break;

        default:
            BTP_Back();
        }
    }
    else
        switch (xSystem.Keypad.ID_Req)
        {
        case WD_MTAB_SETUP:
            MTAB_CLIB_Update_Fr_Keypad();
            break;

        case WD_INFO_ENTER:
            Info_Enter_Update_Fr_Keypad();
            break;

        default:
            break;
        }
    xSystem.Keypad.ID_Req = 0;
}

void CreateKeyInput_Window(void)
{
    xSystem.GUI_Window[WD_KEY_INPUT] = GUI_CreateDialogBox(_aDialogCreate, GUI_COUNTOF(_aDialogCreate), _cbDialog, WM_HBKWIN, 0, 0);
    OldKey = 1;
}

